mapper.select:
SELECT 

FROM f_franchiser a

WHERE a.id = #{id}

SELECT
FROM f_franchiser a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM f_franchiser a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE f_franchiser SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
